.security-level-1 {
  @apply bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200;
}

.security-level-2 {
  @apply bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200;
}

.security-level-3 {
  @apply bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200;
}

.security-level-4 {
  @apply bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200;
}

/* Custom scrollbar for better UX */
.custom-scrollbar::-webkit-scrollbar {
  width: 6px;
}

.custom-scrollbar::-webkit-scrollbar-track {
  background: hsl(var(--muted));
}

.custom-scrollbar::-webkit-scrollbar-thumb {
  background: hsl(var(--muted-foreground));
  border-radius: 3px;
}

.custom-scrollbar::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--foreground));
}

/* Prevent file input interference with navigation */
input[type="file"] {
  pointer-events: all !important;
}

input[type="file"]:not(:hover) {
  pointer-events: none !important;
}

.file-upload-area:hover input[type="file"] {
  pointer-events: all !important;
}

/* Quantum key visualization */
.key-capacity-bar {
  background: linear-gradient(90deg, hsl(var(--accent)) 0%, hsl(var(--primary)) 100%);
  transition: width 0.3s ease-in-out;
}

/* Email encryption status indicators */
.encryption-status-encrypted {
  @apply text-amber-600 bg-amber-50 dark:bg-amber-950 dark:text-amber-400;
}

.encryption-status-decrypted {
  @apply text-green-600 bg-green-50 dark:bg-green-950 dark:text-green-400;
}

.encryption-status-plain {
  @apply text-gray-600 bg-gray-50 dark:bg-gray-950 dark:text-gray-400;
}

/* Enhance focus states for accessibility */
.focus-quantum {
  @apply focus:ring-2 focus:ring-accent focus:ring-offset-2 focus:outline-none;
}

/* Loading state enhancements */
.quantum-loading {
  background: linear-gradient(90deg, hsl(var(--muted)) 25%, hsl(var(--accent)) 50%, hsl(var(--muted)) 75%);
  background-size: 200% 100%;
  animation: quantum-shimmer 2s infinite;
}

@keyframes quantum-shimmer {
  0% {
    background-position: 200% 0;
  }
  100% {
    background-position: -200% 0;
  }
}

