import { useQuery } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Skeleton } from "@/components/ui/skeleton";
import SecurityBadge from "./security-badge";
import { Paperclip, CheckCircle, Lock, AlertCircle } from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import type { Message } from "@/lib/types";

interface EmailListProps {
  folder?: string;
  selectedMessageId?: string;
  onSelectMessage: (message: Message) => void;
}

export default function EmailList({ 
  folder = "inbox", 
  selectedMessageId, 
  onSelectMessage 
}: EmailListProps) {
  const { data: messages, isLoading, error } = useQuery({
    queryKey: ["/api/emails", folder],
    queryFn: () => api.getEmails(folder),
    refetchInterval: 30000 // Refresh every 30 seconds
  });

  if (isLoading) {
    return (
      <div className="p-4 space-y-4">
        {Array.from({ length: 5 }).map((_, i) => (
          <Card key={i} className="p-4">
            <div className="flex items-start space-x-3">
              <Skeleton className="w-10 h-10 rounded-full" />
              <div className="flex-1 space-y-2">
                <Skeleton className="h-4 w-3/4" />
                <Skeleton className="h-3 w-1/2" />
                <Skeleton className="h-3 w-full" />
              </div>
            </div>
          </Card>
        ))}
      </div>
    );
  }

  if (error) {
    return (
      <div className="p-8 text-center">
        <AlertCircle className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
        <h3 className="text-lg font-medium text-foreground mb-2">Failed to load emails</h3>
        <p className="text-sm text-muted-foreground">Please try again later.</p>
      </div>
    );
  }

  if (!messages || messages.length === 0) {
    return (
      <div className="p-8 text-center">
        <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
          <svg
            className="w-8 h-8 text-muted-foreground"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M3 8l7.89 7.89a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"
            />
          </svg>
        </div>
        <h3 className="text-lg font-medium text-foreground mb-2">No messages</h3>
        <p className="text-sm text-muted-foreground">
          {folder === "inbox" ? "Your inbox is empty." : `No messages in ${folder}.`}
        </p>
      </div>
    );
  }

  const getFromName = (email: string) => {
    const match = email.match(/^(.+?)\s*<.*>$/);
    return match ? match[1].trim() : email.split('@')[0];
  };

  const getInitials = (name: string) => {
    return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
  };

  const getEncryptionStatus = (message: Message) => {
    if (!message.isEncrypted) return null;

    if (message.isDecrypted) {
      return {
        icon: CheckCircle,
        text: "Decrypted",
        color: "text-green-600"
      };
    } else {
      return {
        icon: Lock,
        text: "Awaiting decryption", 
        color: "text-amber-600"
      };
    }
  };

  return (
    <div className="h-full overflow-y-auto">
      {messages.map((message) => {
        const fromName = getFromName(message.from);
        const encryptionStatus = getEncryptionStatus(message);
        const isSelected = selectedMessageId === message.id;

        return (
          <div
            key={message.id}
            className={`border-b border-border p-4 cursor-pointer transition-colors hover:bg-muted ${
              isSelected ? 'bg-blue-50 dark:bg-blue-950/50' : ''
            }`}
            onClick={() => onSelectMessage(message)}
            data-testid={`email-item-${message.id}`}
          >
            <div className="flex items-start space-x-3">
              <Avatar className="w-10 h-10">
                <AvatarFallback className="bg-primary text-primary-foreground text-sm">
                  {getInitials(fromName)}
                </AvatarFallback>
              </Avatar>

              <div className="flex-1 min-w-0">
                <div className="flex items-center justify-between mb-1">
                  <p className="text-sm font-semibold text-foreground truncate" data-testid="text-sender">
                    {fromName}
                  </p>
                  <div className="flex items-center space-x-2 flex-shrink-0">
                    <SecurityBadge level={message.securityLevel} size="sm" />
                    <span className="text-xs text-muted-foreground" data-testid="text-time">
                      {formatDistanceToNow(new Date(message.receivedAt), { addSuffix: true })}
                    </span>
                  </div>
                </div>

                <p className="text-sm text-foreground mb-1 truncate" data-testid="text-subject">
                  {message.subject}
                </p>

                <p className="text-sm text-muted-foreground mb-2 truncate" data-testid="text-preview">
                  {message.isDecrypted ? 
                    (message.body ? message.body.substring(0, 100) + "..." : "No preview available") :
                    message.isEncrypted ? "Encrypted message - decrypt to view" : "No preview available"
                  }
                </p>

                <div className="flex items-center space-x-3 text-xs">
                  {message.attachments && message.attachments.length > 0 && (
                    <span className="inline-flex items-center text-muted-foreground">
                      <Paperclip className="mr-1 h-3 w-3" />
                      {message.attachments.length} attachment{message.attachments.length > 1 ? 's' : ''}
                    </span>
                  )}

                  {encryptionStatus && (
                    <span className={`inline-flex items-center ${encryptionStatus.color}`}>
                      <encryptionStatus.icon className="mr-1 h-3 w-3" />
                      {encryptionStatus.text}
                    </span>
                  )}
                </div>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
}

client/src/components/email-preview.tsx.content:
import { useMutation } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import SecurityBadge from "./security-badge";
import { useToast } from "@/hooks/use-toast";
import {
  Reply,
  ReplyAll,
  Forward,
  MoreVertical,
  Shield,
  Download,
  Unlock,
  CheckCircle
} from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import type { Message } from "@/lib/types";

interface EmailPreviewProps {
  message: Message | null;
}

export default function EmailPreview({ message }: EmailPreviewProps) {
  const { toast } = useToast();

  const decryptMutation = useMutation({
    mutationFn: (messageId: string) => api.decryptEmail(messageId),
    onSuccess: async (data, messageId) => {
      // First invalidate all email queries
      await queryClient.invalidateQueries({ queryKey: ["/api/emails"] });

      // Force refetch of current folder emails
      const currentFolder = window.location.pathname === "/sent" ? "sent" : "inbox";
      await queryClient.refetchQueries({ queryKey: ["/api/emails", currentFolder] });

      toast({
        title: "Email decrypted",
        description: "The email has been successfully decrypted.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Decryption failed",
        description: error.message || "Failed to decrypt the email.",
        variant: "destructive",
      });
    }
  });

  if (!message) {
    return (
      <div className="h-full flex items-center justify-center text-center p-8">
        <div>
          <div className="w-16 h-16 bg-muted rounded-full flex items-center justify-center mx-auto mb-4">
            <svg
              className="w-8 h-8 text-muted-foreground"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M3 8l7.89 7.89a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z"
              />
            </svg>
          </div>
          <h3 className="text-lg font-medium text-foreground mb-2">Select an email</h3>
          <p className="text-sm text-muted-foreground">
            Choose an email from the list to view its contents.
          </p>
        </div>
      </div>
    );
  }

  const getFromName = (email: string) => {
    const match = email.match(/^(.+?)\s*<.*>$/);
    return match ? match[1].trim() : email.split('@')[0];
  };

  const getFromEmail = (email: string) => {
    const match = email.match(/<(.+)>/);
    return match ? match[1] : email;
  };

  const getInitials = (name: string) => {
    return name.split(' ').map(n => n[0]).join('').toUpperCase().slice(0, 2);
  };

  const getSecurityInfo = (message: Message) => {
    if (!message.isEncrypted) return null;

    switch (message.securityLevel) {
      case "level1":
        return {
          title: "Level 1 OTP Encrypted",
          details: [
            "Encryption: One-Time Pad (OTP) using quantum keys",
            `Key ID: ${message.keyId || 'Unknown'}`,
            "Authentication: HMAC-SHA256 verified âœ“"
          ]
        };
      case "level2":
        return {
          title: "Level 2 Quantum AES",
          details: [
            "Encryption: AES-256-GCM with quantum-seeded keys",
            `Key ID: ${message.keyId || 'Unknown'}`,
            "Authentication: GCM authenticated encryption"
          ]
        };
      case "level3":
        return {
          title: "Level 3 PQC Hybrid",
          details: [
            "Encryption: Post-Quantum Cryptography hybrid",
            `Key ID: ${message.keyId || 'Unknown'}`,
            "Future-proof against quantum computers"
          ]
        };
      default:
        return {
          title: "Encrypted",
          details: ["Unknown encryption method"]
        };
    }
  };

  const fromName = getFromName(message.from);
  const fromEmail = getFromEmail(message.from);
  const securityInfo = getSecurityInfo(message);
  const canDecrypt = message.isEncrypted && !message.isDecrypted;

  return (
    <div className="h-full flex flex-col">
      {/* Email Header */}
      <div className="border-b border-border p-6">
        <div className="flex items-start justify-between mb-4">
          <div className="flex items-start space-x-3">
            <Avatar className="w-12 h-12">
              <AvatarFallback className="bg-primary text-primary-foreground">
                {getInitials(fromName)}
              </AvatarFallback>
            </Avatar>
            <div>
              <h3 className="text-lg font-semibold text-foreground mb-1" data-testid="text-subject">
                {message.subject}
              </h3>
              <p className="text-sm text-muted-foreground">
                From: <span className="text-foreground" data-testid="text-from">{fromName} &lt;{fromEmail}&gt;</span>
              </p>
              <p className="text-sm text-muted-foreground">
                To: <span className="text-foreground" data-testid="text-to">{message.to}</span>
              </p>
              <p className="text-xs text-muted-foreground mt-1" data-testid="text-timestamp">
                {formatDistanceToNow(new Date(message.receivedAt), { addSuffix: true })}
              </p>
            </div>
          </div>
          <div className="flex items-center space-x-2">
            <SecurityBadge level={message.securityLevel} />
            <Button variant="ghost" size="sm">
              <Reply className="h-4 w-4" />
            </Button>
            <Button variant="ghost" size="sm">
              <MoreVertical className="h-4 w-4" />
            </Button>
          </div>
        </div>

        {/* Security Info */}
        {securityInfo && (
          <div className={`p-3 rounded-lg border ${
            message.isDecrypted
              ? 'bg-green-50 border-green-200 dark:bg-green-950 dark:border-green-800'
              : 'bg-amber-50 border-amber-200 dark:bg-amber-950 dark:border-amber-800'
          }`}>
            <div className="flex items-center space-x-2 mb-2">
              <Shield className={`h-4 w-4 ${
                message.isDecrypted ? 'text-green-600' : 'text-amber-600'
              }`} />
              <span className={`text-sm font-medium ${
                message.isDecrypted ? 'text-green-800 dark:text-green-200' : 'text-amber-800 dark:text-amber-200'
              }`}>
                {message.isDecrypted ? 'Quantum Security Active' : 'Encrypted Message'}
              </span>
              {message.isDecrypted && (
                <CheckCircle className="h-4 w-4 text-green-600" />
              )}
            </div>
            <div className={`text-xs space-y-1 ${
              message.isDecrypted ? 'text-green-700 dark:text-green-300' : 'text-amber-700 dark:text-amber-300'
            }`}>
              {securityInfo.details.map((detail, index) => (
                <p key={index}>{detail}</p>
              ))}
            </div>
            {canDecrypt && (
              <Button
                size="sm"
                className="mt-2"
                onClick={() => decryptMutation.mutate(message.id)}
                disabled={decryptMutation.isPending}
                data-testid="button-decrypt"
              >
                <Unlock className="h-3 w-3 mr-1" />
                {decryptMutation.isPending ? "Decrypting..." : "Decrypt Now"}
              </Button>
            )}
          </div>
        )}
      </div>

      {/* Email Content */}
      <div className="flex-1 p-6 overflow-y-auto">
        {message.isDecrypted && message.body ? (
          <div className="prose max-w-none text-foreground" data-testid="email-content">
            <div className="whitespace-pre-wrap">
              {message.body}
            </div>
          </div>
        ) : message.isEncrypted ? (
          <div className="text-center py-8">
            <div className="w-16 h-16 bg-amber-100 dark:bg-amber-900 rounded-full flex items-center justify-center mx-auto mb-4">
              <Shield className="h-8 w-8 text-amber-600" />
            </div>
            <h3 className="text-lg font-medium text-foreground mb-2">Message Encrypted</h3>
            <p className="text-sm text-muted-foreground mb-4">
              This message is encrypted with quantum security. Decrypt it to view the contents.
            </p>
            {canDecrypt && (
              <Button
                onClick={() => decryptMutation.mutate(message.id)}
                disabled={decryptMutation.isPending}
                data-testid="button-decrypt-main"
              >
                <Unlock className="h-4 w-4 mr-2" />
                {decryptMutation.isPending ? "Decrypting..." : "Decrypt Message"}
              </Button>
            )}
          </div>
        ) : (
          <div className="text-center py-8">
            <p className="text-muted-foreground">No content available</p>
          </div>
        )}

        {/* Attachments */}
        {message.attachments && message.attachments.length > 0 && (
          <div className="mt-6 p-4 bg-muted rounded-lg">
            <h4 className="text-sm font-medium text-foreground mb-3 flex items-center">
              <svg className="h-4 w-4 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13" />
              </svg>
              Attachments ({message.attachments.length})
            </h4>
            <div className="space-y-2">
              {message.attachments.map((attachment, index) => (
                <div key={index} className="flex items-center justify-between p-3 bg-card border border-border rounded-md">
                  <div className="flex items-center space-x-3">
                    <div className="w-8 h-8 bg-primary/10 rounded flex items-center justify-center">
                      <svg className="h-4 w-4 text-primary" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12h6m-6 4h6m2 5H7a2 2 0 01-2-2V5a2 2 0 012-2h5.586a1 1 0 01.707.293l5.414 5.414a1 1 0 01.293.707V19a2 2 0 01-2 2z" />
                      </svg>
                    </div>
                    <div>
                      <p className="text-sm font-medium text-foreground">{attachment.filename}</p>
                      <p className="text-xs text-muted-foreground">
                        {(attachment.size / 1024).toFixed(1)} KB â€¢ {attachment.contentType}
                      </p>
                    </div>
                  </div>
                  <Button size="sm" variant="outline" data-testid={`button-download-${index}`}>
                    <Download className="h-3 w-3 mr-1" />
                    Download
                  </Button>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Reply Actions */}
      <div className="border-t border-border p-4 bg-card">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <Button size="sm" data-testid="button-reply">
              <Reply className="h-4 w-4 mr-2" />
              Reply
            </Button>
            <Button size="sm" variant="outline" data-testid="button-reply-all">
              <ReplyAll className="h-4 w-4 mr-2" />
              Reply All
            </Button>
            <Button size="sm" variant="outline" data-testid="button-forward">
              <Forward className="h-4 w-4 mr-2" />
              Forward
            </Button>
          </div>
          <div className="flex items-center space-x-2">
            <select className="px-3 py-1 text-sm bg-background border border-border rounded-md focus:outline-none focus:ring-2 focus:ring-ring">
              <option value="level1">Level 1 - Quantum OTP</option>
              <option value="level2">Level 2 - Quantum AES</option>
              <option value="level3">Level 3 - PQC Hybrid</option>
              <option value="level4">Level 4 - Plain Text</option>
            </select>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/components/security-badge.tsx.content:
import { Badge } from "@/components/ui/badge";
import { Shield, Key, Lock, Mail } from "lucide-react";
import { SecurityLevel } from "@/lib/types";

interface SecurityBadgeProps {
  level: SecurityLevel;
  size?: "sm" | "md" | "lg";
  showIcon?: boolean;
}

export default function SecurityBadge({ level, size = "md", showIcon = true }: SecurityBadgeProps) {
  const getSecurityConfig = (level: SecurityLevel) => {
    switch (level) {
      case SecurityLevel.LEVEL1_OTP:
        return {
          label: "Level 1 OTP",
          description: "One-Time Pad",
          color: "bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200",
          icon: Shield
        };
      case SecurityLevel.LEVEL2_AES:
        return {
          label: "Level 2 AES", 
          description: "Quantum-seeded AES",
          color: "bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200",
          icon: Key
        };
      case SecurityLevel.LEVEL3_PQC:
        return {
          label: "Level 3 PQC",
          description: "CRYSTALS-Kyber PQC",
          color: "bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200",
          icon: Lock
        };
      case SecurityLevel.LEVEL4_PLAIN:
        return {
          label: "Plain Text",
          description: "No encryption",
          color: "bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200",
          icon: Mail
        };
      default:
        return {
          label: "Unknown",
          description: "Unknown security level",
          color: "bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-200",
          icon: Mail
        };
    }
  };

  const config = getSecurityConfig(level);
  const Icon = config.icon;

  const sizeClasses = {
    sm: "text-xs px-2 py-0.5",
    md: "text-sm px-2 py-1", 
    lg: "text-base px-3 py-1.5"
  };

  const iconSizes = {
    sm: "h-3 w-3",
    md: "h-4 w-4",
    lg: "h-5 w-5"
  };

  return (
    <Badge 
      className={`${config.color} ${sizeClasses[size]} inline-flex items-center space-x-1 border-0`}
      data-testid={`badge-security-${level}`}
    >
      {showIcon && <Icon className={iconSizes[size]} />}
      <span>{config.label}</span>
    </Badge>
  );
}

client/src/components/sidebar.tsx.content:
import { useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import { 
  Shield, 
  Inbox, 
  Send, 
  Key, 
  Trash, 
  Settings, 
  ShieldCheck,
  LogOut
} from "lucide-react";

interface SidebarProps {
  unreadCount?: number;
}

export default function Sidebar({ unreadCount = 0 }: SidebarProps) {
  const [location, setLocation] = useLocation();
  const { toast } = useToast();

  const { data: userInfo } = useQuery({
    queryKey: ["/api/auth/me"],
    queryFn: () => api.getMe()
  });

  const { data: keyPoolStats } = useQuery({
    queryKey: ["/api/keys/pool"],
    queryFn: () => api.getKeyPool(),
    refetchInterval: 30000 // Refresh every 30 seconds
  });

  const logoutMutation = useMutation({
    mutationFn: () => api.logout(),
    onSuccess: () => {
      // Clear all queries and reset query client
      queryClient.clear();
      queryClient.resetQueries();
      // Force redirect to login
      window.location.href = "/login";
    },
    onError: () => {
      // Even if logout fails on server, clear client state
      queryClient.clear();
      queryClient.resetQueries();
      window.location.href = "/login";
    }
  });

  const navItems = [
    { 
      path: "/inbox", 
      icon: Inbox, 
      label: "Inbox", 
      badge: unreadCount > 0 ? unreadCount.toString() : undefined 
    },
    { path: "/sent", icon: Send, label: "Sent" },
    { path: "/compose", icon: Send, label: "Compose" },
    { path: "/keys", icon: Key, label: "Key Dashboard" },
    { path: "/settings", icon: Settings, label: "Settings" },
  ];

  const getSecurityStatus = () => {
    if (!keyPoolStats) return { level: "Unknown", color: "bg-gray-500" };

    if (keyPoolStats.utilizationPercent < 50) {
      return { level: "Quantum Active", color: "bg-green-500" };
    } else if (keyPoolStats.utilizationPercent < 80) {
      return { level: "Quantum Low", color: "bg-yellow-500" };
    } else {
      return { level: "Quantum Critical", color: "bg-red-500" };
    }
  };

  const securityStatus = getSecurityStatus();

  if (!userInfo?.user) {
    return null;
  }

  const user = userInfo.user;

  return (
    <div className="w-64 bg-card border-r border-border flex flex-col h-screen">
      {/* Logo and Title */}
      <div className="p-6 border-b border-border">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-primary rounded-lg flex items-center justify-center">
            <Shield className="h-6 w-6 text-primary-foreground" />
          </div>
          <div>
            <h1 className="text-xl font-bold text-foreground">QuMail</h1>
            <p className="text-xs text-muted-foreground">Quantum Secure Email</p>
          </div>
        </div>
      </div>

      {/* User Info */}
      <div className="p-4 border-b border-border">
        <div className="flex items-center space-x-3 mb-3">
          <Avatar>
            <AvatarFallback className="bg-primary text-primary-foreground">
              {user.username?.charAt(0).toUpperCase() || user.email.charAt(0).toUpperCase()}
            </AvatarFallback>
          </Avatar>
          <div className="flex-1 min-w-0">
            <p className="text-sm font-medium text-foreground truncate" data-testid="text-username">
              {user.username || user.email.split('@')[0]}
            </p>
            <p className="text-xs text-muted-foreground truncate" data-testid="text-email">
              {user.email}
            </p>
          </div>
        </div>

        {/* Security Status */}
        <div className="p-2 bg-green-50 dark:bg-green-950 rounded-md border border-green-200 dark:border-green-800">
          <div className="flex items-center justify-between mb-1">
            <span className="text-xs font-medium text-green-800 dark:text-green-200">
              Security Status
            </span>
            <span className="text-xs text-green-600 dark:text-green-400" data-testid="text-security-status">
              {securityStatus.level}
            </span>
          </div>
          <div className="w-full bg-green-200 dark:bg-green-900 rounded-full h-1.5">
            <div 
              className={`${securityStatus.color} h-1.5 rounded-full transition-all`}
              style={{ width: `${100 - (keyPoolStats?.utilizationPercent || 0)}%` }}
            ></div>
          </div>
        </div>
      </div>

      {/* Navigation */}
      <nav className="flex-1 p-4 space-y-2">
        {navItems.map((item) => {
          const Icon = item.icon;
          const isActive = location === item.path;

          return (
            <Button
              key={item.path}
              variant={isActive ? "default" : "ghost"}
              className={`w-full justify-start ${isActive ? 'bg-primary text-primary-foreground' : 'text-muted-foreground hover:bg-muted hover:text-foreground'}`}
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                setLocation(item.path);
              }}
              data-testid={`nav-${item.label.toLowerCase().replace(' ', '-')}`}
            >
              <Icon className="h-4 w-4 mr-3" />
              <span className="text-sm font-medium">{item.label}</span>
              {item.badge && (
                <Badge 
                  variant={isActive ? "secondary" : "default"} 
                  className="ml-auto"
                  data-testid="badge-unread-count"
                >
                  {item.badge}
                </Badge>
              )}
            </Button>
          );
        })}

        <div className="pt-4">
          <Button
            variant="ghost"
            className="w-full justify-start text-muted-foreground hover:bg-muted hover:text-foreground"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              setLocation("/audit");
            }}
            data-testid="nav-security-audit"
          >
            <ShieldCheck className="h-4 w-4 mr-3" />
            <span className="text-sm font-medium">Security Audit</span>
          </Button>

          <Button
            variant="ghost"
            size="sm"
            className="w-full justify-start text-destructive hover:text-destructive"
            onClick={(e) => {
              e.preventDefault();
              e.stopPropagation();
              logoutMutation.mutate();
            }}
            data-testid="button-logout"
          >
            <LogOut className="h-4 w-4 mr-3" />
            <span className="text-sm font-medium">
              {logoutMutation.isPending ? "Logging out..." : "Logout"}
            </span>
          </Button>
        </div>
      </nav>

      {/* Key Usage Status */}
      <div className="p-4 border-t border-border">
        <div className="bg-muted rounded-lg p-3">
          <div className="flex items-center justify-between mb-2">
            <span className="text-xs font-medium text-muted-foreground">Quantum Keys</span>
            <span className="text-xs text-foreground" data-testid="text-key-capacity">
              {keyPoolStats ? `${keyPoolStats.remainingMB} MB` : "Loading..."}
            </span>
          </div>
          <Progress 
            value={keyPoolStats ? 100 - keyPoolStats.utilizationPercent : 0} 
            className="h-2"
            data-testid="progress-key-usage"
          />
          <p className="text-xs text-muted-foreground mt-1">
            {keyPoolStats ? `${100 - keyPoolStats.utilizationPercent}% capacity remaining` : "Loading..."}
          </p>
        </div>
      </div>
    </div>
  );
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-toast.ts.content:
import * as React from "react"

import type {
  ToastActionElement,
  ToastProps,
} from "@/components/ui/toast"

const TOAST_LIMIT = 1
const TOAST_REMOVE_DELAY = 1000000

type ToasterToast = ToastProps & {
  id: string
  title?: React.ReactNode
  description?: React.ReactNode
  action?: ToastActionElement
}

const actionTypes = {
  ADD_TOAST: "ADD_TOAST",
  UPDATE_TOAST: "UPDATE_TOAST",
  DISMISS_TOAST: "DISMISS_TOAST",
  REMOVE_TOAST: "REMOVE_TOAST",
} as const

let count = 0

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER
  return count.toString()
}

type ActionType = typeof actionTypes

type Action =
  | {
      type: ActionType["ADD_TOAST"]
      toast: ToasterToast
    }
  | {
      type: ActionType["UPDATE_TOAST"]
      toast: Partial<ToasterToast>
    }
  | {
      type: ActionType["DISMISS_TOAST"]
      toastId?: ToasterToast["id"]
    }
  | {
      type: ActionType["REMOVE_TOAST"]
      toastId?: ToasterToast["id"]
    }

interface State {
  toasts: ToasterToast[]
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>()

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId)
    dispatch({
      type: "REMOVE_TOAST",
      toastId: toastId,
    })
  }, TOAST_REMOVE_DELAY)

  toastTimeouts.set(toastId, timeout)
}

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case "ADD_TOAST":
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      }

    case "UPDATE_TOAST":
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      }

    case "DISMISS_TOAST": {
      const { toastId } = action

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId)
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id)
        })
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      }
    }
    case "REMOVE_TOAST":
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        }
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      }
  }
}

const listeners: Array<(state: State) => void> = []

let memoryState: State = { toasts: [] }

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action)
  listeners.forEach((listener) => {
    listener(memoryState)
  })
}

type Toast = Omit<ToasterToast, "id">

function toast({ ...props }: Toast) {
  const id = genId()

  const update = (props: ToasterToast) =>
    dispatch({
      type: "UPDATE_TOAST",
      toast: { ...props, id },
    })
  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id })

  dispatch({
    type: "ADD_TOAST",
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss()
      },
    },
  })

  return {
    id: id,
    dismiss,
    update,
  }
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState)

  React.useEffect(() => {
    listeners.push(setState)
    return () => {
      const index = listeners.indexOf(setState)
      if (index > -1) {
        listeners.splice(index, 1)
      }
    }
  }, [state])

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
  }
}

export { useToast, toast }

client/src/lib/api.ts.content:
import { apiRequest } from "./queryClient";
import type { User, Message, QuantumKey, KeyPoolStats, AuditLog, SendEmailRequest } from "./types";

export const api = {
  // Authentication
  async register(userData: { username: string; email: string; password: string }) {
    const response = await apiRequest("POST", "/api/auth/register", userData);
    return response.json();
  },

  async login(credentials: { email: string; password: string }) {
    const response = await apiRequest("POST", "/api/auth/login", credentials);
    return response.json();
  },

  async logout() {
    const response = await apiRequest("POST", "/api/auth/logout");
    return response.json();
  },

  async getMe(): Promise<{ user: User }> {
    const response = await apiRequest("GET", "/api/auth/me");
    return response.json();
  },

  // Email operations
  async getEmails(folder = "inbox"): Promise<Message[]> {
    const response = await apiRequest("GET", `/api/emails?folder=${folder}`);
    return response.json();
  },

  async getEmail(messageId: string): Promise<Message> {
    const response = await apiRequest("GET", `/api/emails/${messageId}`);
    return response.json();
  },

  async sendEmail(emailData: SendEmailRequest) {
    const response = await apiRequest("POST", "/api/emails/send", emailData);
    return response.json();
  },

  async decryptEmail(messageId: string) {
    const response = await apiRequest("POST", `/api/emails/${messageId}/decrypt`);
    return response.json();
  },

  async fetchEmails() {
    const response = await apiRequest("POST", "/api/emails/fetch");
    return response.json();
  },

  // Key management
  async getKeyPool(): Promise<KeyPoolStats> {
    const response = await apiRequest("GET", "/api/keys/pool");
    return response.json();
  },

  async getKeys(): Promise<QuantumKey[]> {
    const response = await apiRequest("GET", "/api/keys");
    return response.json();
  },

  async requestKey(keyLength = 8192, recipient?: string) {
    const response = await apiRequest("POST", "/api/keys/request", { keyLength, recipient });
    return response.json();
  },

  // Audit logs
  async getAuditLogs(limit = 50): Promise<AuditLog[]> {
    const response = await apiRequest("GET", `/api/audit?limit=${limit}`);
    return response.json();
  },

  // User settings
  async updateSettings(settings: Record<string, any>) {
    const response = await apiRequest("PUT", "/api/user/settings", settings);
    return response.json();
  }
};

