import React, { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import Sidebar from "@/components/sidebar";
import EmailList from "@/components/email-list";
import EmailPreview from "@/components/email-preview";
import ComposeModal from "@/components/compose-modal";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  Search, 
  Plus, 
  RefreshCw, 
  AlertCircle,
  CheckCircle 
} from "lucide-react";
import type { Message } from "@/lib/types";

export default function Inbox() {
  const { toast } = useToast();
  const [selectedMessage, setSelectedMessage] = useState<Message | null>(null);
  const [searchQuery, setSearchQuery] = useState("");
  const [isComposeOpen, setIsComposeOpen] = useState(false);

  // Get current folder from URL path
  const [location] = useLocation();
  const currentFolder = location === "/sent" ? "sent" : "inbox";

  const { data: messages, isLoading, error, refetch } = useQuery({
    queryKey: ["/api/emails", currentFolder],
    queryFn: () => api.getEmails(currentFolder),
    refetchInterval: 30000 // Auto-refresh every 30 seconds
  });

  // Update selected message when messages data changes
  React.useEffect(() => {
    if (selectedMessage && messages) {
      const updatedMessage = messages.find(msg => msg.id === selectedMessage.id);
      if (updatedMessage && updatedMessage.isDecrypted !== selectedMessage.isDecrypted) {
        setSelectedMessage(updatedMessage);
      }
    }
  }, [messages, selectedMessage]);

  const { data: userInfo } = useQuery({
    queryKey: ["/api/auth/me"],
    queryFn: () => api.getMe()
  });

  const fetchEmailsMutation = useMutation({
    mutationFn: () => api.fetchEmails(),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/emails"] });
      toast({
        title: "Emails updated",
        description: "Successfully fetched latest emails from your provider.",
        variant: "default",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Sync failed",
        description: error.message || "Failed to fetch emails from your provider.",
        variant: "destructive",
      });
    }
  });

  const handleSelectMessage = (message: Message) => {
    setSelectedMessage(message);
  };

  const handleRefresh = () => {
    fetchEmailsMutation.mutate();
  };

  const getUnreadCount = () => {
    if (!messages) return 0;
    return messages.filter(msg => !msg.isRead).length;
  };

  const getLastSyncTime = () => {
    return "2 minutes ago"; // This would come from actual sync metadata
  };

  if (!userInfo?.user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background flex">
      <Sidebar unreadCount={getUnreadCount()} />

      <div className="flex-1 flex flex-col">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <div className="flex items-center space-x-3">
                <h2 className="text-2xl font-semibold text-foreground" data-testid="page-title">
                  {currentFolder.charAt(0).toUpperCase() + currentFolder.slice(1)}
                </h2>
                {isLoading && (
                  <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-primary"></div>
                )}
                {error && (
                  <Badge variant="destructive" className="text-xs">
                    <AlertCircle className="h-3 w-3 mr-1" />
                    Error
                  </Badge>
                )}
              </div>
              <p className="text-sm text-muted-foreground mt-1">
                {messages ? (
                  <>
                    {getUnreadCount()} unread messages â€¢ Last sync: {getLastSyncTime()}
                  </>
                ) : (
                  "Loading messages..."
                )}
              </p>
            </div>

            <div className="flex items-center space-x-4">
              {/* Search */}
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
                <Input
                  type="text"
                  placeholder="Search messages..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10 pr-4 py-2 w-64 text-sm"
                  data-testid="input-search"
                />
              </div>

              {/* Actions */}
              <Button
                onClick={() => setIsComposeOpen(true)}
                className="flex items-center space-x-2"
                data-testid="button-compose"
              >
                <Plus className="h-4 w-4" />
                <span>Compose</span>
              </Button>

              <Button
                variant="ghost"
                size="sm"
                onClick={handleRefresh}
                disabled={fetchEmailsMutation.isPending}
                data-testid="button-refresh"
              >
                <RefreshCw className={`h-4 w-4 ${fetchEmailsMutation.isPending ? 'animate-spin' : ''}`} />
              </Button>
            </div>
          </div>
        </header>

        {/* Main Content */}
        <div className="flex-1 flex overflow-hidden">
          {/* Email List */}
          <div className="w-1/2 border-r border-border">
            <EmailList
              folder={currentFolder}
              selectedMessageId={selectedMessage?.id}
              onSelectMessage={handleSelectMessage}
            />
          </div>

          {/* Email Preview */}
          <div className="w-1/2">
            <EmailPreview message={selectedMessage} />
          </div>
        </div>
      </div>

      {/* Compose Modal */}
      <ComposeModal
        isOpen={isComposeOpen}
        onClose={() => setIsComposeOpen(false)}
      />
    </div>
  );
}

client/src/pages/key-dashboard.tsx.content:
import { useQuery, useMutation } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import Sidebar from "@/components/sidebar";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Skeleton } from "@/components/ui/skeleton";
import { 
  Key, 
  Shield, 
  Plus, 
  RefreshCw, 
  AlertTriangle,
  CheckCircle,
  Clock,
  Database,
  TrendingUp,
  Activity
} from "lucide-react";
import { formatDistanceToNow } from "date-fns";
import type { QuantumKey, KeyPoolStats } from "@/lib/types";

export default function KeyDashboard() {
  const { toast } = useToast();

  const { data: keyPoolStats, isLoading: statsLoading, isFetching: statsFetching } = useQuery({
    queryKey: ["/api/keys/pool"],
    queryFn: () => api.getKeyPool(),
    refetchInterval: 2000 // Refresh every 2 seconds for live updates
  });

  const { data: keys, isLoading: keysLoading, isFetching: keysFetching } = useQuery({
    queryKey: ["/api/keys"],
    queryFn: () => api.getKeys(),
    refetchInterval: 3000 // Refresh every 3 seconds for live updates
  });

  const { data: userInfo, isFetching: userFetching } = useQuery({
    queryKey: ["/api/auth/me"],
    queryFn: () => api.getMe(),
    refetchInterval: 5000 // Refresh every 5 seconds
  });

  const requestKeyMutation = useMutation({
    mutationFn: ({ keyLength, recipient }: { keyLength: number; recipient?: string }) => 
      api.requestKey(keyLength, recipient),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/keys"] });
      queryClient.invalidateQueries({ queryKey: ["/api/keys/pool"] });
      toast({
        title: "Quantum key requested",
        description: "A new quantum key has been generated and added to your pool.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Key request failed",
        description: error.message || "Failed to request new quantum key.",
        variant: "destructive",
      });
    }
  });

  const handleRequestKey = (keyLength: number = 8192) => {
    requestKeyMutation.mutate({ keyLength });
  };

  const getKeyStatusColor = (key: QuantumKey) => {
    if (!key.isActive) return "bg-gray-100 text-gray-800";
    if (key.utilizationPercent >= 90) return "bg-red-100 text-red-800";
    if (key.utilizationPercent >= 70) return "bg-yellow-100 text-yellow-800";
    return "bg-green-100 text-green-800";
  };

  const getKeyStatusText = (key: QuantumKey) => {
    if (!key.isActive) return "Exhausted";
    if (key.utilizationPercent >= 90) return "Critical";
    if (key.utilizationPercent >= 70) return "Low";
    return "Active";
  };

  const getPoolHealthStatus = () => {
    if (!keyPoolStats) return { status: "Unknown", color: "text-gray-600", icon: Clock };

    if (keyPoolStats.utilizationPercent <= 30) {
      return { status: "Excellent", color: "text-green-600", icon: CheckCircle };
    } else if (keyPoolStats.utilizationPercent <= 60) {
      return { status: "Good", color: "text-blue-600", icon: TrendingUp };
    } else if (keyPoolStats.utilizationPercent <= 80) {
      return { status: "Warning", color: "text-yellow-600", icon: AlertTriangle };
    } else {
      return { status: "Critical", color: "text-red-600", icon: AlertTriangle };
    }
  };

  const poolHealth = getPoolHealthStatus();

  if (!userInfo?.user) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">Loading...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background flex">
      <Sidebar />

      <div className="flex-1 flex flex-col">
        {/* Header */}
        <header className="bg-card border-b border-border px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h2 className="text-2xl font-semibold text-foreground flex items-center space-x-2">
                <Key className="h-6 w-6" />
                <span>Quantum Key Dashboard</span>
              </h2>
              <p className="text-sm text-muted-foreground mt-1">
                Manage your quantum key pool and monitor security capacity
              </p>
            </div>

            <div className="flex items-center space-x-3">
              {(statsFetching || keysFetching || userFetching) && (
                <div className="flex items-center space-x-2 text-sm text-muted-foreground">
                  <div className="w-2 h-2 bg-primary rounded-full animate-pulse"></div>
                  <span>Live updating...</span>
                </div>
              )}

              <Button
                onClick={() => handleRequestKey(8192)}
                disabled={requestKeyMutation.isPending}
                data-testid="button-request-key"
              >
                <Plus className="h-4 w-4 mr-2" />
                {requestKeyMutation.isPending ? "Requesting..." : "Request New Key"}
              </Button>

              <Button
                variant="ghost"
                size="sm"
                onClick={() => {
                  queryClient.invalidateQueries({ queryKey: ["/api/keys"] });
                  queryClient.invalidateQueries({ queryKey: ["/api/keys/pool"] });
                }}
                data-testid="button-refresh"
                className={statsFetching || keysFetching ? "animate-spin" : ""}
              >
                <RefreshCw className="h-4 w-4" />
              </Button>
            </div>
          </div>
        </header>

        <div className="flex-1 p-6 overflow-y-auto">
          <div className="max-w-7xl mx-auto space-y-6">
            {/* Stats Cards */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
              {statsLoading ? (
                Array.from({ length: 4 }).map((_, i) => (
                  <Card key={i}>
                    <CardContent className="p-6">
                      <Skeleton className="h-4 w-20 mb-2" />
                      <Skeleton className="h-8 w-16 mb-2" />
                      <Skeleton className="h-3 w-24" />
                    </CardContent>
                  </Card>
                ))
              ) : keyPoolStats ? (
                <>
                  <Card className={statsFetching ? "ring-2 ring-primary/20 transition-all duration-300" : ""}>
                    <CardContent className="p-6">
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="flex items-center gap-2">
                            <p className="text-sm font-medium text-muted-foreground">Total Capacity</p>
                            {statsFetching && <div className="w-1 h-1 bg-primary rounded-full animate-pulse"></div>}
                          </div>
                          <p className="text-2xl font-bold text-foreground" data-testid="text-total-capacity">
                            {keyPoolStats.totalCapacityMB} MB
                          </p>
                          <p className="text-xs text-muted-foreground">
                            {keyPoolStats.totalKeys} keys
                          </p>
                        </div>
                        <Database className="h-8 w-8 text-primary" />
                      </div>
                    </CardContent>
                  </Card>

                  <Card className={statsFetching ? "ring-2 ring-green-200 transition-all duration-300" : ""}>
                    <CardContent className="p-6">
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="flex items-center gap-2">
                            <p className="text-sm font-medium text-muted-foreground">Available</p>
                            {statsFetching && <div className="w-1 h-1 bg-green-500 rounded-full animate-pulse"></div>}
                          </div>
                          <p className="text-2xl font-bold text-foreground" data-testid="text-available">
                            {keyPoolStats.remainingMB} MB
                          </p>
                          <p className="text-xs text-green-600">
                            {(100 - keyPoolStats.utilizationPercent).toFixed(1)}% free
                          </p>
                        </div>
                        <CheckCircle className="h-8 w-8 text-green-600" />
                      </div>
                    </CardContent>
                  </Card>

                  <Card className={statsFetching ? "ring-2 ring-blue-200 transition-all duration-300" : ""}>
                    <CardContent className="p-6">
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="flex items-center gap-2">
                            <p className="text-sm font-medium text-muted-foreground">Consumed</p>
                            {statsFetching && <div className="w-1 h-1 bg-blue-500 rounded-full animate-pulse"></div>}
                          </div>
                          <p className="text-2xl font-bold text-foreground" data-testid="text-consumed">
                            {keyPoolStats.consumedMB} MB
                          </p>
                          <p className="text-xs text-muted-foreground">
                            {keyPoolStats.utilizationPercent.toFixed(1)}% used
                          </p>
                        </div>
                        <Activity className={`h-8 w-8 text-blue-600 ${statsFetching ? 'animate-pulse' : ''}`} />
                      </div>
                    </CardContent>
                  </Card>

                  <Card className={statsFetching ? `ring-2 ${poolHealth.status === 'Critical' ? 'ring-red-200' : 'ring-green-200'} transition-all duration-300` : ""}>
                    <CardContent className="p-6">
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="flex items-center gap-2">
                            <p className="text-sm font-medium text-muted-foreground">Pool Health</p>
                            {statsFetching && <div className={`w-1 h-1 rounded-full animate-pulse ${poolHealth.status === 'Critical' ? 'bg-red-500' : 'bg-green-500'}`}></div>}
                          </div>
                          <p className={`text-2xl font-bold ${poolHealth.color}`} data-testid="text-pool-health">
                            {poolHealth.status}
                          </p>
                          <p className="text-xs text-muted-foreground">
                            Security status
                          </p>
                        </div>
                        <poolHealth.icon className={`h-8 w-8 ${poolHealth.color} ${statsFetching ? 'animate-pulse' : ''}`} />
                      </div>
                    </CardContent>
                  </Card>
                </>
              ) : null}
            </div>

            {/* Key Pool Usage */}
            {keyPoolStats && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <TrendingUp className="h-5 w-5" />
                    <span>Key Pool Usage</span>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    <div className="flex justify-between items-center">
                      <span className="text-sm font-medium">Utilization</span>
                      <span className="text-sm text-muted-foreground">
                        {keyPoolStats.consumedMB} / {keyPoolStats.totalCapacityMB} MB
                      </span>
                    </div>
                    <Progress 
                      value={keyPoolStats.utilizationPercent} 
                      className="h-3"
                      data-testid="progress-utilization"
                    />
                    <div className="flex justify-between text-xs text-muted-foreground">
                      <span>0%</span>
                      <span className="font-medium">{keyPoolStats.utilizationPercent.toFixed(1)}% used</span>
                      <span>100%</span>
                    </div>

                    {keyPoolStats.utilizationPercent > 80 && (
                      <div className="flex items-center space-x-2 p-3 bg-amber-50 dark:bg-amber-950 border border-amber-200 dark:border-amber-800 rounded-md">
                        <AlertTriangle className="h-4 w-4 text-amber-600" />
                        <div>
                          <p className="text-sm font-medium text-amber-800 dark:text-amber-200">
                            Key pool running low
                          </p>
                          <p className="text-xs text-amber-700 dark:text-amber-300">
                            Consider requesting additional quantum keys to maintain security levels.
                          </p>
                        </div>
                      </div>
                    )}
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Individual Keys */}
            <Card className={keysFetching ? "ring-2 ring-primary/20 transition-all duration-300" : ""}>
              <CardHeader>
                <CardTitle className="flex items-center justify-between">
                  <div className="flex items-center space-x-2">
                    <Shield className="h-5 w-5" />
                    <span>Quantum Keys</span>
                    {keysFetching && <div className="w-2 h-2 bg-primary rounded-full animate-pulse"></div>}
                  </div>
                  {keys && (
                    <Badge variant="secondary" data-testid="badge-key-count">
                      {keys.length} keys
                    </Badge>
                  )}
                </CardTitle>
              </CardHeader>
              <CardContent>
                {keysLoading ? (
                  <div className="space-y-4">
                    {Array.from({ length: 3 }).map((_, i) => (
                      <div key={i} className="flex items-center space-x-4 p-4 border border-border rounded-lg">
                        <Skeleton className="w-12 h-12 rounded" />
                        <div className="flex-1 space-y-2">
                          <Skeleton className="h-4 w-32" />
                          <Skeleton className="h-3 w-48" />
                        </div>
                        <Skeleton className="h-8 w-16" />
                      </div>
                    ))}
                  </div>
                ) : keys && keys.length > 0 ? (
                  <div className="space-y-4">
                    {keys.map((key) => (
                      <div
                        key={key.id}
                        className="flex items-center justify-between p-4 border border-border rounded-lg hover:bg-muted/50 transition-colors"
                        data-testid={`key-item-${key.id}`}
                      >
                        <div className="flex items-center space-x-4">
                          <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center">
                            <Key className="h-6 w-6 text-primary" />
                          </div>
                          <div>
                            <p className="font-medium text-foreground" data-testid="text-key-id">
                              {key.keyId}
                            </p>
                            <p className="text-sm text-muted-foreground">
                              {(key.keyLength / 1024).toFixed(1)} KB â€¢ 
                              Created {formatDistanceToNow(new Date(key.createdAt), { addSuffix: true })}
                            </p>
                            <p className="text-xs text-muted-foreground">
                              Expires {formatDistanceToNow(new Date(key.expiryTime), { addSuffix: true })}
                            </p>
                          </div>
                        </div>

                        <div className="flex items-center space-x-4">
                          <div className="text-right min-w-[100px]">
                            <p className="text-sm font-medium text-foreground">
                              {key.utilizationPercent}% used
                            </p>
                            <p className="text-xs text-muted-foreground">
                              {key.consumedBytes} / {key.maxConsumptionBytes} bytes
                            </p>
                            <Progress 
                              value={key.utilizationPercent} 
                              className="h-1 w-20 mt-1"
                            />
                          </div>

                          <Badge 
                            className={getKeyStatusColor(key)}
                            data-testid="badge-key-status"
                          >
                            {getKeyStatusText(key)}
                          </Badge>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <Key className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-foreground mb-2">No quantum keys</h3>
                    <p className="text-sm text-muted-foreground mb-4">
                      Request your first quantum key to start sending secure messages.
                    </p>
                    <Button onClick={() => handleRequestKey(8192)}>
                      <Plus className="h-4 w-4 mr-2" />
                      Request First Key
                    </Button>
                  </div>
                )}
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

client/src/pages/login.tsx.content:
import React, { useState } from "react";
import { useLocation } from "wouter";
import { useMutation, useQuery } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { useToast } from "@/hooks/use-toast";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Shield, Mail } from "lucide-react";
import { useQueryClient } from "@tanstack/react-query";

export default function Login() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isLogin, setIsLogin] = useState(true);

  const [loginForm, setLoginForm] = useState({
    email: "",
    password: ""
  });

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");


  // Check if already logged in
  const { data: userInfo, isLoading } = useQuery({
    queryKey: ["/api/auth/me"],
    queryFn: () => api.getMe(),
    retry: false,
    staleTime: 0, // Always check for fresh data
    gcTime: 0 // Don't cache the result
  });

  // Use useEffect to handle redirection to avoid hooks order issues
  React.useEffect(() => {
    if (userInfo?.user && !isLoading) {
      setLocation("/inbox");
    }
  }, [userInfo, isLoading, setLocation]);

  const loginMutation = useMutation({
    mutationFn: (credentials: { email: string; password: string }) =>
      api.login(credentials),
    onSuccess: () => {
      toast({
        title: "Login successful",
        description: "Welcome to QuMail!",
      });
      setLocation("/inbox");
    },
    onError: (error: any) => {
      toast({
        title: "Login failed",
        description: error.message || "Invalid credentials",
        variant: "destructive",
      });
    }
  });

  const registerMutation = useMutation({
    mutationFn: async (userData: { username: string; email: string; password: string }) => {
      return api.register(userData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      setLocation("/inbox");
      toast({
        title: "Account created",
        description: "Welcome to QuMail! Your secure internal email platform.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Registration failed",
        description: error.message || "Please try again.",
        variant: "destructive",
      });
    }
  });

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    loginMutation.mutate(loginForm);
  };

  const handleRegister = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!username || !email || !password) {
      toast({
        title: "Missing fields",
        description: "Please fill in all fields.",
        variant: "destructive",
      });
      return;
    }
    registerMutation.mutate({ username, email, password });
  };

  return (
    <div className="min-h-screen bg-background flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        {/* Logo */}
        <div className="text-center mb-8">
          <div className="flex items-center justify-center space-x-3 mb-4">
            <div className="w-12 h-12 bg-primary rounded-lg flex items-center justify-center">
              <Shield className="h-7 w-7 text-primary-foreground" />
            </div>
            <div>
              <h1 className="text-3xl font-bold text-foreground">QuMail</h1>
              <p className="text-sm text-muted-foreground">Quantum Secure Email</p>
            </div>
          </div>
          <p className="text-sm text-muted-foreground">
            Secure your communications with quantum encryption
          </p>
        </div>

        <Card>
          <CardHeader>
            <CardTitle className="text-center">
              {isLogin ? "Sign In" : "Create Account"}
            </CardTitle>
          </CardHeader>
          <CardContent>
            <Tabs value={isLogin ? "login" : "register"} onValueChange={(value) => setIsLogin(value === "login")}>
              <TabsList className="grid w-full grid-cols-2">
                <TabsTrigger value="login" data-testid="tab-login">Sign In</TabsTrigger>
                <TabsTrigger value="register" data-testid="tab-register">Sign Up</TabsTrigger>
              </TabsList>

              <TabsContent value="login">
                <form onSubmit={handleLogin} className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="email">Email</Label>
                    <Input
                      id="email"
                      type="email"
                      value={loginForm.email}
                      onChange={(e) => setLoginForm({ ...loginForm, email: e.target.value })}
                      placeholder="your.email@example.com"
                      required
                      data-testid="input-email"
                    />
                  </div>
                  <div className="space-y-2">
                    <Label htmlFor="password">Password</Label>
                    <Input
                      id="password"
                      type="password"
                      value={loginForm.password}
                      onChange={(e) => setLoginForm({ ...loginForm, password: e.target.value })}
                      placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                      required
                      data-testid="input-password"
                    />
                  </div>
                  <Button
                    type="submit"
                    className="w-full"
                    disabled={loginMutation.isPending}
                    data-testid="button-login"
                  >
                    {loginMutation.isPending ? "Signing In..." : "Sign In"}
                    <Mail className="ml-2 h-4 w-4" />
                  </Button>
                </form>
              </TabsContent>

              <TabsContent value="register">
                  <form onSubmit={handleRegister} className="space-y-4">
                    <div>
                      <Label htmlFor="username">Username</Label>
                      <Input
                        id="username"
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                        data-testid="input-username"
                      />
                    </div>
                    <div>
                      <Label htmlFor="register-email">Email Address</Label>
                      <Input
                        id="register-email"
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="your.email@qumail.com"
                        required
                        data-testid="input-register-email"
                      />
                      <p className="text-xs text-muted-foreground mt-1">
                        This will be your QuMail address for secure internal communication
                      </p>
                    </div>
                    <div>
                      <Label htmlFor="register-password">Password</Label>
                      <Input
                        id="register-password"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        data-testid="input-register-password"
                      />
                    </div>
                    <Button
                      type="submit"
                      className="w-full"
                      disabled={registerMutation.isPending}
                      data-testid="button-register"
                    >
                      {registerMutation.isPending ? (
                        <>
                          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                          Creating Account...
                        </>
                      ) : (
                        "Create Account"
                      )}
                    </Button>
                  </form>
                </TabsContent>

            </Tabs>

            <div className="mt-6 p-4 bg-muted rounded-lg">
              <div className="flex items-center space-x-2 mb-2">
                <Shield className="h-4 w-4 text-accent" />
                <span className="text-sm font-medium">Quantum Security Features</span>
              </div>
              <ul className="text-xs text-muted-foreground space-y-1">
                <li>â€¢ Quantum Key Distribution (QKD) simulation</li>
                <li>â€¢ One-Time Pad encryption for maximum security</li>
                <li>â€¢ Post-Quantum Cryptography readiness</li>
                <li>â€¢ End-to-end encrypted communication</li>
              </ul>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

